by_name.map:
function (doc, meta) {
  if(doc.DocType && doc.DocType == "Contractor") {
     emit(doc.Code, doc.Id);
   }
}

by_date.map:
function (doc, meta) {
  if(doc.DocType && doc.DocType == "Document") {
     emit(doc.DocumentDate, doc.Id);
   }
}

by_login.map:
function (doc, meta) {
  if(doc.DocType && doc.DocType == "User")
  	emit(meta.id, null);
}

by_login_active.map:
function (doc, meta) {
  if(doc.DocType && doc.DocType == "User" && doc.IsActive) {
  	emit(meta.id, null);
  }
}
